private void defaultDlvMode()
{
    this.DeliveryModeCode = TMSCarrierService::findDlvModeId(this.ShippingCarrierId, this.ShippingCarrierServiceId);
    if (!this.DeliveryModeCode)
    {
        CustTable requestingCustomer = CustTable::find(this.OrderingCustomerAccountNumber);
        this.DeliveryModeCode = requestingCustomer.DlvMode;
    }
}

/// <summary>
/// Retrieves the defaulting dependencies.
/// </summary>
/// <returns>A container with the defaulting dependencies.</returns>
public container getDefaultingDependencies()
{
    return [
        [fieldNum(AAXSalesOrderHeaderV2EntityNoGlobalization, DeliveryModeCode),
            [fieldNum(AAXSalesOrderHeaderV2EntityNoGlobalization, ShippingCarrierServiceId),
            fieldNum(AAXSalesOrderHeaderV2EntityNoGlobalization, ShippingCarrierId)]],
        [fieldNum(AAXSalesOrderHeaderV2EntityNoGlobalization, DeliveryAddressDescription),
            [fieldNum(AAXSalesOrderHeaderV2EntityNoGlobalization, DeliveryAddressCountryRegionId)]]
    ];
}

/// <summary>
/// Populates default values in a field.
/// </summary>
/// <param name = "_fieldId">The Id of the field that gets defaulted.</param>
public void defaultField(FieldId _fieldId)
{
    super (_fieldId);

    switch (_fieldId)
    {
        case fieldNum(AAXSalesOrderHeaderV2EntityNoGlobalization, DeliveryModeCode):
            this.defaultDlvMode();
            break;
        case fieldNum(AAXSalesOrderHeaderV2EntityNoGlobalization, DeliveryAddressDescription):
            if (!this.DeliveryAddressDescription && this.DeliveryAddressCountryRegionId)
            {
                this.defaultDeliveryAddressDescription();
            }
            break;
    }
}

private void defaultDeliveryAddressDescription()
{
    CustTable requestingCustomer = CustTable::find(this.OrderingCustomerAccountNumber);
    if (requestingCustomer.RecId)
    {
        this.DeliveryAddressDescription = requestingCustomer.name();
    }
}

public void initializeEntityDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    super(_entityCtx, _dataSourceCtx);

    switch (_dataSourceCtx.name())
    {
        case dataEntityDataSourceStr(AAXSalesOrderHeaderV2EntityNoGlobalization, SalesTable):
            // clear out 'isFieldSet' so the defaulting logic does not consider fields that were set by initValue as specified by the user
            _dataSourceCtx.setAllFieldStatesToNotSet();
            break;
    }
}

public Common findEntityDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    Common ret;

    switch (_dataSourceCtx.name())
    {
        case dataEntityDataSourceStr(AAXSalesOrderHeaderV2EntityNoGlobalization, LogisticsPostalAddressBaseEntity):
            ret = TradeEntitySpecificDeliveryAddress::findLogisticsPostalAddressBaseEntityDataSource();
            break;

        case dataEntityDataSourceStr(AAXSalesOrderHeaderV2EntityNoGlobalization, TMSSalesTable):
            ret = TMSSalesTable::find(this.SalesOrderNumber, true);
            break;

        default:
            ret = super(_entityCtx, _dataSourceCtx);
    }

    return ret;
}

public boolean insertEntityDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    switch (_dataSourceCtx.name())
    {
        case dataEntityDataSourceStr(AAXSalesOrderHeaderV2EntityNoGlobalization, SalesTable):
            this.validateWriteSalesTableDataSource(_dataSourceCtx);
            break;

        case dataEntityDataSourceStr(AAXSalesOrderHeaderV2EntityNoGlobalization, TMSSalesTable):
            _entityCtx.detectConflictForDatasource(_dataSourceCtx);
            break;
    }

    boolean ret = super(_entityCtx, _dataSourceCtx);

    switch (_dataSourceCtx.name())
    {
        case dataEntityDataSourceStr(AAXSalesOrderHeaderV2EntityNoGlobalization, LogisticsPostalAddressBaseEntity):
            this.TradeEntitySpecificDeliveryAddress::postInsertLogisticsPostalAddressBaseEntityDataSource(_dataSourceCtx);
            break;
    }

    return ret;
}

public boolean updateEntityDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    switch (_dataSourceCtx.name())
    {
        case dataEntityDataSourceStr(AAXSalesOrderHeaderV2EntityNoGlobalization, SalesTable):
            if (this.CurrencyCode != this.orig().CurrencyCode)
            {
                SalesTable salesTable = _dataSourceCtx.getBuffer();
                salesTable.convertCurrencyCode(this.CurrencyCode, TradeCurencyConversion::TradeAgreements);
            }
            this.validateWriteSalesTableDataSource(_dataSourceCtx);
            break;

        case dataEntityDataSourceStr(AAXSalesOrderHeaderV2EntityNoGlobalization, TMSSalesTable):
            _entityCtx.detectConflictForDatasource(_dataSourceCtx);
            break;
    }

    return super(_entityCtx, _dataSourceCtx);
}

private void validateWriteSalesTableDataSource(DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    SalesTable salesTable = _dataSourceCtx.getBuffer() as SalesTable;
    SalesTableType salesTableType = salesTable.type();

    boolean isValid = true;

    if (this.isFieldModified(fieldNum(AAXSalesOrderHeaderV2EntityNoGlobalization, OrderingCustomerAccountNumber)) && !salesTableType.editCustAccount())
    {
        isValid = this.allowEditCheckFailed(fieldPName(AAXSalesOrderHeaderV2EntityNoGlobalization, OrderingCustomerAccountNumber));
    }

    if (this.isFieldModified(fieldNum(AAXSalesOrderHeaderV2EntityNoGlobalization, InvoiceCustomerAccountNumber)) && !salesTableType.editInvoiceAccount())
    {
        isValid = this.allowEditCheckFailed(fieldPName(AAXSalesOrderHeaderV2EntityNoGlobalization, InvoiceCustomerAccountNumber));
    }

    if (this.isFieldModified(fieldNum(AAXSalesOrderHeaderV2EntityNoGlobalization, TaxExemptNumber)) && !salesTableType.editVATNum())
    {
        isValid = this.allowEditCheckFailed(fieldPName(AAXSalesOrderHeaderV2EntityNoGlobalization, TaxExemptNumber));
    }

    if (this.isFieldModified(fieldNum(AAXSalesOrderHeaderV2EntityNoGlobalization, ArePricesIncludingSalesTax)) && !salesTableType.editInclTax())
    {
        isValid = this.allowEditCheckFailed(fieldPName(AAXSalesOrderHeaderV2EntityNoGlobalization, ArePricesIncludingSalesTax));
    }

    if (this.isFieldModified(fieldNum(AAXSalesOrderHeaderV2EntityNoGlobalization, CurrencyCode)) && !salesTableType.editCurrencyCode())
    {
        isValid = this.allowEditCheckFailed(fieldPName(AAXSalesOrderHeaderV2EntityNoGlobalization, CurrencyCode));
    }

    if (this.isFieldModified(fieldNum(AAXSalesOrderHeaderV2EntityNoGlobalization, DirectDebitMandateId)) && !CustPaymModeTable::find(salesTable.PaymMode).IsSEPA)
    {
        isValid = this.allowEditCheckFailed(fieldPName(AAXSalesOrderHeaderV2EntityNoGlobalization, DirectDebitMandateId));
    }

    if (salesTable.isMarkedForBankLC())
    {
        List fields = new List(Types::Integer);
        fields.addEnd(fieldNum(AAXSalesOrderHeaderV2EntityNoGlobalization, PaymentTermsName));
        fields.addEnd(fieldNum(AAXSalesOrderHeaderV2EntityNoGlobalization, FixedDueDate));
        fields.addEnd(fieldNum(AAXSalesOrderHeaderV2EntityNoGlobalization, CustomerPaymentMethodName));
        fields.addEnd(fieldNum(AAXSalesOrderHeaderV2EntityNoGlobalization, CustomerPaymentMethodSpecificationName));
        fields.addEnd(fieldNum(AAXSalesOrderHeaderV2EntityNoGlobalization, PaymentScheduleName));
        fields.addEnd(fieldNum(AAXSalesOrderHeaderV2EntityNoGlobalization, CashDiscountCode));
        fields.addEnd(fieldNum(AAXSalesOrderHeaderV2EntityNoGlobalization, CustomerTransactionSettlementType));
        isValid = isValid & this.checkFieldsAreNotModified(fields);
    }

    if (this.isFieldModified(fieldNum(AAXSalesOrderHeaderV2EntityNoGlobalization, WillAutomaticInventoryReservationConsiderBatchAttributes)) && !salesTable.canEditPdsBatchAttribAutoRes())
    {
        isValid = this.allowEditCheckFailed(fieldPName(AAXSalesOrderHeaderV2EntityNoGlobalization, WillAutomaticInventoryReservationConsiderBatchAttributes));
    }

    if (!isValid)
    {
        _dataSourceCtx.throwValidateWriteFailed();
    }
}

