}

private boolean allowEditCheckFailed(FieldLabel _fieldLabel)
{
    return checkFailed(strFmt("@ApplicationFoundation:DataEntitiesNoAllowEdit", _fieldLabel));
}

private boolean isFieldModified(FieldId _fieldId)
{
    return DataEntityDataSourceRuntimeContextAppSuite_Extension::isFieldModified(this, _fieldId);
}

private boolean checkFieldsAreNotModified(List _fieldIds)
{
    var enumerator = _fieldIds.getEnumerator();
    boolean result = true;

    while (enumerator.moveNext())
    {
        FieldId fieldId = enumerator.current();
        if (this.isFieldModified(fieldId))
        {
            result = this.allowEditCheckFailed(fieldId2PName(tableNum(AAXSalesOrderHeaderV2EntityNoGlobalization), fieldId));
        }
    }

    return result;
}

public static str computeExportSales()
{
    TableName viewName = tableStr(AAXSalesOrderHeaderV2EntityNoGlobalization);

    return SysComputedColumn::if(
        SysComputedColumn::isNullExpression(
            SysComputedColumn::comparisonField(viewName, identifierStr(ExportSalesOrder), fieldStr(ExportSalesOrder, RecId))),
        SysComputedColumn::returnLiteral(NoYes::No),
        SysComputedColumn::returnLiteral(NoYes::Yes));
}

public static Query defaultCTQuery()
{
    Query salesOrderHeaderQuery = new Query();

    var salesTableDS = salesOrderHeaderQuery.addDataSource(tableNum(SalesTable));

    var custDirectDebitMandateDS = salesTableDS.addDataSource(tableNum(CustDirectDebitMandate));
    custDirectDebitMandateDS.joinMode(JoinMode::OuterJoin);
    custDirectDebitMandateDS.relations(true);

    var orderResponsibleWorkerDS = salesTableDS.addDataSource(tableNum(HcmWorker));
    orderResponsibleWorkerDS.joinMode(JoinMode::OuterJoin);
    orderResponsibleWorkerDS.addLink(fieldNum(SalesTable, WorkerSalesResponsible), fieldNum(HcmWorker, RecId));

    var orderTakerWorkerDS = salesTableDS.addDataSource(tableNum(HcmWorker));
    orderTakerWorkerDS.joinMode(JoinMode::OuterJoin);
    orderTakerWorkerDS.addLink(fieldNum(SalesTable, WorkerSalesTaker), fieldNum(HcmWorker, RecId));

    var dimensionSetEntityDS = salesTableDS.addDataSource(tableNum(DimensionAttributeValueSet));
    dimensionSetEntityDS.joinMode(JoinMode::OuterJoin);
    dimensionSetEntityDS.addLink(fieldNum(SalesTable, DefaultDimension), fieldNum(DimensionAttributeValueSet, RecId));

    var salesTableWDS = salesTableDS.addDataSource(tableNum(SalesTable_W));
    salesTableWDS.joinMode(JoinMode::OuterJoin);
    salesTableWDS.addLink(fieldNum(SalesTable, RecId), fieldNum(SalesTable_W, SalesTable));  

    var tmsSalesTableDS = salesTableDS.addDataSource(tableNum(TMSSalesTable));
    tmsSalesTableDS.joinMode(JoinMode::OuterJoin);
    tmsSalesTableDS.addLink(fieldNum(SalesTable, SalesId), fieldNum(TMSSalesTable, SalesId));

    LogisticsPostalAddressBaseEntityChangeTrackingHelper::addPostalAddressBasedOnAddressRecIdLink(salesTableDS, fieldNum(SalesTable, DeliveryPostalAddress));

    var creditNoteReasonDS = salesTableDS.addDataSource(tableNum(ReasonTableRef));
    creditNoteReasonDS.joinMode(JoinMode::OuterJoin);
    creditNoteReasonDS.addLink(fieldNum(SalesTable, CreditNoteReasonCode), fieldNum(ReasonTableRef, RecId));

    var exportSalesOrderDS = salesTableDS.addDataSource(tableNum(ExportSalesOrder));
    exportSalesOrderDS.joinMode(JoinMode::OuterJoin);
    exportSalesOrderDS.addLink(fieldnum(SalesTable, SalesId), fieldNum(ExportSalesOrder, SalesId));

    var salesTableSymbolDS = salesTableDS.addDataSource(tableNum(SalesTableSymbol));
    salesTableSymbolDS.joinMode(JoinMode::OuterJoin);
    salesTableSymbolDS.addLink(fieldNum(SalesTable, RecId), fieldNum(SalesTableSymbol, SalesTable));

    var bankConstantSymbolDS = salesTableSymbolDS.addDataSource(tableNum(BankConstantSymbol));
    bankConstantSymbolDS.joinMode(JoinMode::OuterJoin);
    bankConstantSymbolDS.addLink(fieldNum(SalesTableSymbol, BankConstantSymbol), fieldNum(BankConstantSymbol, RecId));

    var salesTableBRDS = salesTableDS.addDataSource(tableNum(SalesTable_BR));
    salesTableBRDS.joinMode(JoinMode::OuterJoin);
    salesTableBRDS.addLink(fieldNum(SalesTable, RecId), fieldNum(SalesTable_BR, SalesTable));

    var salesPurchOpTypeBRDS = salesTableBRDS.addDataSource(tableNum(SalesPurchOperationType_BR));
    salesPurchOpTypeBRDS.joinMode(JoinMode::OuterJoin);
    salesPurchOpTypeBRDS.addLink(fieldNum(SalesTable_BR, SalesPurchOperationType_BR), fieldNum(SalesPurchOperationType_BR, RecId));

    var fiscalDocTypeBRDS = salesTableBRDS.addDataSource(tableNum(FiscalDocumentType_BR));
    fiscalDocTypeBRDS.joinMode(JoinMode::OuterJoin);
    fiscalDocTypeBRDS.addLink(fieldNum(SalesTable_BR, FiscalDocumentType_BR), fieldNum(FiscalDocumentType_BR, RecId));

    var custDefaultLocationDS = salesTableDS.addDataSource(tableNum(CustDefaultLocation));
    custDefaultLocationDS.joinMode(JoinMode::OuterJoin);
    custDefaultLocationDS.addLink(fieldNum(SalesTable, InvoiceAccount), fieldNum(CustDefaultLocation,AccountNum));

    var dirPartyLocationRoleDS = custDefaultLocationDS.addDataSource(tableNum(DirPartyLocationRole));
    dirPartyLocationRoleDS.addLink(fieldNum(CustDefaultLocation, PartyLocationRole), fieldNum(DirPartyLocationRole, RecId));

    var logisticsLocationRoleDS = dirPartyLocationRoleDS.addDataSource(tableNum(LogisticsLocationRole));
    logisticsLocationRoleDS.addLink(fieldNum(DirPartyLocationRole, LocationRole), fieldNum(LogisticsLocationRole, RecId));

    var dirPartyLocationDS = dirPartyLocationRoleDS.addDataSource(tableNum(DirPartyLocation));
    dirPartyLocationDS.addLink(fieldNum(DirPartyLocationRole, PartyLocation), fieldNum(DirPartyLocation, RecId));

    var LogisticsPostalAddress = dirPartyLocationDS.addDataSource(tableNum(LogisticsPostalAddress));
    LogisticsPostalAddress.addLink(fieldNum(DirPartyLocation, Location), fieldNum(LogisticsPostalAddress, Location));

    var salesOrderTotalsDS = salesTableDS.addDataSource(tableNum(SalesOrderTotals));
    salesOrderTotalsDS.joinMode(JoinMode::OuterJoin);
    salesOrderTotalsDS.addLink(fieldNum(SalesTable, SalesId), fieldNum(SalesOrderTotals, SalesId));

    var transportDocDS = salesTableDS.addDataSource(tableNum(TransportationDocument));
    transportDocDS.joinMode(JoinMode::OuterJoin);
    transportDocDS.addLink(fieldNum(SalesTable, TransportationDocument), fieldNum(TransportationDocument, RecId));

    return salesOrderHeaderQuery;
}

