public boolean insertEntityDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    switch (_dataSourceCtx.name())
    {
        case dataEntityDataSourceStr(AAXSalesOrderHeaderV2EntityOnlySalesTable, SalesTable):
            this.validateWriteSalesTableDataSource(_dataSourceCtx);
            break;
    }

    boolean ret = super(_entityCtx, _dataSourceCtx);

    return ret;
}

public boolean updateEntityDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    switch (_dataSourceCtx.name())
    {
        case dataEntityDataSourceStr(AAXSalesOrderHeaderV2EntityOnlySalesTable, SalesTable):
            if (this.CurrencyCode != this.orig().CurrencyCode)
            {
                SalesTable salesTable = _dataSourceCtx.getBuffer();
                salesTable.convertCurrencyCode(this.CurrencyCode, TradeCurencyConversion::TradeAgreements);
            }
            this.validateWriteSalesTableDataSource(_dataSourceCtx);
            break;

    }

    return super(_entityCtx, _dataSourceCtx);
}

private void validateWriteSalesTableDataSource(DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    SalesTable salesTable = _dataSourceCtx.getBuffer() as SalesTable;
    SalesTableType salesTableType = salesTable.type();

    boolean isValid = true;

    if (this.isFieldModified(fieldNum(AAXSalesOrderHeaderV2EntityOnlySalesTable, OrderingCustomerAccountNumber)) && !salesTableType.editCustAccount())
    {
        isValid = this.allowEditCheckFailed(fieldPName(AAXSalesOrderHeaderV2EntityOnlySalesTable, OrderingCustomerAccountNumber));
    }

    if (this.isFieldModified(fieldNum(AAXSalesOrderHeaderV2EntityOnlySalesTable, InvoiceCustomerAccountNumber)) && !salesTableType.editInvoiceAccount())
    {
        isValid = this.allowEditCheckFailed(fieldPName(AAXSalesOrderHeaderV2EntityOnlySalesTable, InvoiceCustomerAccountNumber));
    }

    if (this.isFieldModified(fieldNum(AAXSalesOrderHeaderV2EntityOnlySalesTable, TaxExemptNumber)) && !salesTableType.editVATNum())
    {
        isValid = this.allowEditCheckFailed(fieldPName(AAXSalesOrderHeaderV2EntityOnlySalesTable, TaxExemptNumber));
    }

    if (this.isFieldModified(fieldNum(AAXSalesOrderHeaderV2EntityOnlySalesTable, ArePricesIncludingSalesTax)) && !salesTableType.editInclTax())
    {
        isValid = this.allowEditCheckFailed(fieldPName(AAXSalesOrderHeaderV2EntityOnlySalesTable, ArePricesIncludingSalesTax));
    }

    if (this.isFieldModified(fieldNum(AAXSalesOrderHeaderV2EntityOnlySalesTable, CurrencyCode)) && !salesTableType.editCurrencyCode())
    {
        isValid = this.allowEditCheckFailed(fieldPName(AAXSalesOrderHeaderV2EntityOnlySalesTable, CurrencyCode));
    }



    if (salesTable.isMarkedForBankLC())
    {
        List fields = new List(Types::Integer);
        fields.addEnd(fieldNum(AAXSalesOrderHeaderV2EntityOnlySalesTable, PaymentTermsName));
        fields.addEnd(fieldNum(AAXSalesOrderHeaderV2EntityOnlySalesTable, FixedDueDate));
        fields.addEnd(fieldNum(AAXSalesOrderHeaderV2EntityOnlySalesTable, CustomerPaymentMethodName));
        fields.addEnd(fieldNum(AAXSalesOrderHeaderV2EntityOnlySalesTable, CustomerPaymentMethodSpecificationName));
        fields.addEnd(fieldNum(AAXSalesOrderHeaderV2EntityOnlySalesTable, PaymentScheduleName));
        fields.addEnd(fieldNum(AAXSalesOrderHeaderV2EntityOnlySalesTable, CashDiscountCode));
        fields.addEnd(fieldNum(AAXSalesOrderHeaderV2EntityOnlySalesTable, CustomerTransactionSettlementType));
        isValid = isValid & this.checkFieldsAreNotModified(fields);
    }

    if (this.isFieldModified(fieldNum(AAXSalesOrderHeaderV2EntityOnlySalesTable, WillAutomaticInventoryReservationConsiderBatchAttributes)) && !salesTable.canEditPdsBatchAttribAutoRes())
    {
        isValid = this.allowEditCheckFailed(fieldPName(AAXSalesOrderHeaderV2EntityOnlySalesTable, WillAutomaticInventoryReservationConsiderBatchAttributes));
    }

    if (!isValid)
    {
        _dataSourceCtx.throwValidateWriteFailed();
    }
}

